import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ResultsPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const result = location.state?.result;

  if (!result) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
        <p className="text-lg text-red-500">No results available. Please submit your data first.</p>
        <button
          className="mt-4 p-2 bg-blue-500 text-white rounded-md"
          onClick={() => navigate("/")}
        >
          Go Back
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <div className="mt-6 p-4 bg-white rounded-lg shadow-md max-w-md">
        <h2 className="text-xl font-semibold mb-4">Diet Plan Results</h2>
        <p><strong>BMI:</strong> {result.BMI}</p>
        <p><strong>BMR:</strong> {result.BMR}</p>
        <p><strong>Daily Maintenance Calories:</strong> {result.Daily_Calories_Maintenance}</p>
        <p><strong>Target Calories Per Day:</strong> {result.Daily_Calories_Target}</p>

        <h3 className="text-lg font-semibold mt-4">Recommended Diet:</h3>
        <ul className="list-disc ml-6">
          <li><strong>Breakfast:</strong> {result.Predicted_Diet.Breakfast}</li>
          <li><strong>Lunch:</strong> {result.Predicted_Diet.Lunch}</li>
          <li><strong>Dinner:</strong> {result.Predicted_Diet.Dinner}</li>
          <li><strong>Snack:</strong> {result.Predicted_Diet.Snack}</li>
        </ul>

        <button
          className="mt-6 p-2 bg-blue-500 text-white rounded-md"
          onClick={() => navigate("/")}
        >
          Go Back
        </button>
      </div>
    </div>
  );
};

export default ResultsPage;
