import React, { useState } from "react";
import UserInputForm from "./components/UserInputForm";

const App = () => {
  const [result, setResult] = useState(null);

  const handleFormSubmit = async (data) => {
    console.log("User Input Data:", data);

    try {
      const response = await fetch("http://127.0.0.1:5000/submit-data", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();
      console.log("Server Response:", result);
      setResult(result);
    } catch (error) {
      console.error("Error sending data to backend:", error);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <UserInputForm onSubmit={handleFormSubmit} />

      {result && (
        <div className="mt-6 p-4 bg-white rounded-lg shadow-md max-w-md">
          <h2 className="text-xl font-semibold mb-4">Results</h2>
          <p><strong>BMI:</strong> {result.BMI}</p>
          <p><strong>BMR:</strong> {result.BMR}</p>
          <p><strong>Target Calories Per Day:</strong> {result.TargetCaloriesPerDay}</p>
        </div>
      )}
    </div>
  );
};

export default App;
